	FROM tblClaim C
	INNER JOIN tblClaimItems CI ON CI.ClaimId = C.ClaimID
	INNER JOIN tblHF HF ON HF.HFID = C.HFID
	INNER JOIN uvwLocations L ON L.DistrictId = HF.LocationId
	WHERE C.ValidityTo IS NULL
