	GROUP BY ProdId, HF.HFID, C.ClaimID, C.ClaimCode
	UNION
	SELECT CS.ProdId, HF.HFID ,C.ClaimID
	FROM tblClaim C
	INNER JOIN tblClaimServices CS ON CS.ClaimId = C.ClaimID
	INNER JOIN tblHF HF ON HF.HFID = C.HFID
