	WHERE [tblRelDistr].RowID  > @RowID
	    AND (L.RegionId = @RegionId OR @RegionId =0 OR L.LocationId =0  )
		AND (L.DistrictId =@DistrictId OR L.DistrictId IS NULL OR @DistrictId =0 )

