CREATE PROCEDURE [uspSSRSPolicyRenewalPromptJournal]

	@RangeFrom date = NULL,
	@RangeTo date = NULL,
	@IntervalType as tinyint = 1 ,     -- 1 = Prompt Date in prompting table ; 2 = Expiry Date search in prompting table
	@OfficerID int = 0,
	@LocationId as int = 0,
