	--BATCH RUN INFORMATION
	--UNION ALL
	IF @EntityId = N'BR' OR @EntityId = N''
		INSERT INTO @tblLogs(UserId,UserName,EntityId,RecordType,ActionType,RecordIdentity,ValidityFrom,ValidityTo,LegacyId,VF,HistoryLegacyId)
